/*
  eslint-disable
  import/unambiguous,
  import/no-commonjs,
  import/no-dynamic-require
*/

module.exports = function babelPrep (webpackConfig, test) {
  const babelConfig = webpackConfig.module.rules.filter((rule) => {
    return rule.test.toString().includes(test);
  })[0].loaders.filter((loader) => {
    return loader.loader && loader.loader.includes('babel-loader');
  })[0].options;

  const preparedBabelConfig = Object.assign(
    {},
    babelConfig,
    {
      plugins: [].concat(
        babelConfig.plugins.filter((plugin) => {
          return ![
            '@babel/plugin-transform-regenerator',
            '@babel/plugin-syntax-dynamic-import',
            'babel-plugin-transform-node-env-inline',
            'babel-plugin-transform-inline-environment-variables'
          ].includes(plugin[0]);
        }),
        []
      ),
      presets: babelConfig.presets.filter((preset) => {
        return preset[0] !== 'babel-preset-minify';
      })
    }
  );

  preparedBabelConfig.presets[preparedBabelConfig.presets.findIndex((preset) => {
    return preset[0] === '@babel/preset-env';
  })][1].modules = 'umd';

  return preparedBabelConfig;
};

/*
  eslint-enable
  import/unambiguous,
  import/no-commonjs,
  import/no-dynamic-require
*/
