const fs = require('fs');
const path = require('path');
const rimraf = require('rimraf');
const seleniumStandAlone = require('selenium-standalone');
const {
  binPath,
  chromeDriverVersion,
  geckoDriverVersion,
  ieDriverVersion,
  seleniumPath,
  seleniumVersion
} = require('./vars.conf');

fs.stat(seleniumPath, (err, stat) => { // eslint-disable-line promise/prefer-await-to-callbacks
  if (err || !stat || stat.size < 1) {
    rimraf(path.join(binPath, 'chromedriver'), (rmerr) => {
      if (rmerr) {
        throw new Error(rmerr);
      }

      seleniumStandAlone.install({
        basePath: binPath,
        baseURL: 'https://selenium-release.storage.googleapis.com',
        drivers: {
          chrome: {
            arch: process.arch,
            baseURL: 'https://chromedriver.storage.googleapis.com',
            version: chromeDriverVersion
          },
          firefox: {
            arch: process.arch,
            baseURL: 'https://github.com/mozilla/geckodriver/releases/download',
            version: geckoDriverVersion
          },
          ie: {
            arch: process.arch,
            baseURL: 'https://selenium-release.storage.googleapis.com',
            version: ieDriverVersion
          }
        },
        logger () {},
        progressCb () {},
        version: seleniumVersion
      }, (error) => {
        if (error) {
          throw new Error(error);
        }

        console.log(' \u001B[32mâœ”\u001B[0m Drivers downloaded to:', binPath); // eslint-disable-line no-console
      });
    });
  }
});

