/*
  eslint-disable
  filenames/match-regex,
  import/no-commonjs,
  import/unambiguous
*/

const postcssCssnext = require('postcss-cssnext');
const postcssEach = require('postcss-each');
const postcssImport = require('postcss-import');
const postcssMixins = require('postcss-mixins');
const postcssReporter = require('postcss-reporter');

// Don't use stylelint with postcss because of how poorly it handles errors in webpack

module.exports = ({
  // file,
  options

  // , env
}) => {
  return {
    plugins: [
      postcssImport(options.imp),
      postcssMixins(options.mixins),
      postcssEach(options.each),
      postcssCssnext(options.next),
      postcssReporter(options.reporter)
    ]
  };
};

/*
  eslint-enable
  filenames/match-regex,
  import/no-commonjs,
  import/unambiguous
*/
